import React, { Component } from 'react';
import { Collapse, Button, CardBody, Card } from 'reactstrap';
import MenuItem from './MenuItem';

class Menu extends Component {

    state = { 
        collapse: false,
        Voorgerechten:false,
        Nagerechten:false,
        Hoofdgerechten:false, 
    };

    toggle = () => {
    this.setState({ collapse: !this.state.collapse });
    }

    render() {
    return (
        <div>
            <Button color="primary" onClick={this.toggle} style={{ marginBottom: '1rem',width:100 + '%' }}>Toggle</Button>
            <Collapse isOpen={this.state.collaps}>
                <MenuItem
                    key={this.props.key} 
                    index = {this.props.key}
                    details={this.props.details} 
                    addToOrder={this.props.addToOrder}
                />
            </Collapse>
            <Collapse isOpen={this.state.collapse}>
                <MenuItem
                    key={this.props.key} 
                    index = {this.props.key}
                    details={this.props.details} 
                    addToOrder={this.props.addToOrder}
                />
            </Collapse>
            <Collapse isOpen={this.state.collapse}>
                <MenuItem
                    key={this.props.key} 
                    index = {this.props.key}
                    details={this.props.details} 
                    addToOrder={this.props.addToOrder}
                />
            </Collapse>
        </div>
        );
    }
}

export default Menu;
