import React from 'react';
import { Button, Modal, ModalHeader, ModalBody} from 'reactstrap';
import Inventory from './Inventory';


class InventoryModal extends React.Component {
  state = {
      modal: false
  }

  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  }

  render() {
    return (
      <div className="inventory">
        <Button className="btn-info btn-sm" onClick={this.toggle}>Customize Menu</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle} className="modal-lg">
          <ModalHeader toggle={this.toggle}>Inventory</ModalHeader>
          <ModalBody>
            <Inventory  
                toggle={this.toggle}
                addMenuItem={this.props.addMenuItem}
                deleteMenuItem = {this.props.deleteMenuItem} 
                updateMenuItem = {this.props.updateMenuItem}
                loadSampleFishes={this.props.loadSampleFishes}
                fishes ={this.props.fishes}
                history={this.props.history}
            />
          </ModalBody>
        </Modal>
      </div>
    );
  }
}

export default InventoryModal;
