import React from 'react';
import PropTypes from 'prop-types';

class AddFishForm extends React.Component {

    state = {
        selectedImage:null
    }

    nameRef = React.createRef();
    priceRef = React.createRef();
    typeRef = React.createRef();
    descRef = React.createRef();
    imageRef = React.createRef();
    testRef = React.createRef();

    static propTypes = {
        addMenuItem : PropTypes.func,
    };

    createMenuItem = event => {
        event.preventDefault();
        const menuItem = {
            name : this.nameRef.current.value,
            price : parseFloat(this.priceRef.current.value),
            type : this.typeRef.current.value,
            desc : this.descRef.current.value,
            image : this.imageRef.current.value,
        };
        this.props.addMenuItem(menuItem);
        event.currentTarget.reset();
    }

    fieldChange = event => {
        this.setState({selectedImage : event.target.files[0]},
        () => this.handleImage);
    }

    handleImage = () => {
        // uploadImage(this.state.selectedImage);
    }
    render() {
        console.log(this.state.image);
        return(
            <form className = "menu-edit" onSubmit={this.createMenuItem}>
                <input name="name" ref ={this.nameRef} type="text" placeholder="Name"/>
                <input name="price" ref ={this.priceRef} type="text" placeholder="Price"/>
                <select name="type"ref ={this.typeRef}>
                    <option value="Voorgerechten">Voorgerechten</option>
                    <option value="Hoofdgerechten">Hoofdgerechten</option>
                    <option value="Nagerechten">Nagerechten</option>
                </select>
                <textarea name="desc" ref ={this.descRef} type="text" placeholder="Desc"></textarea>
                <input name="image" ref ={this.imageRef} type="file" placeholder="Image"/>
                <button type="submit" onClick={this.handleImage}></button>
                <button type="submit">Add Menu Item</button>
            </form>
        );
    }
}

export default AddFishForm;
