import React from 'react';
import Order from './Order';
import RoyalHeader from './RoyalHeader';
import Header from './Header';
import sampleFishes from '../sample-fishes';
import Menu from './Menu';
import base from '../base';
import PropTypes from 'prop-types';

class App extends React.Component{
    state = {
        menuItems:{},
        order:{}
    }

    static propTypes = {
        match : PropTypes.object,
    };

    componentDidMount() {
        const {params} = this.props.match;
        const localStorageRef = localStorage.getItem(params);
        if(localStorageRef){
            this.setState({order:JSON.parse(localStorageRef)})
        }
        this.ref = base.syncState(`${params.storeId}/menuItems`,{
            context:this,
            state:'menuItems'
        });
    }

    componentDidUpdate() {
        localStorage.setItem(this.props.match.params.storeId, JSON.stringify(this.state.order));
    }

    componentWillUnmount() {
        base.removeBinding(this.ref);
    }

    updateFish = (key, updatedFish) => {
        // take the copy of the current state
        const menuItems = {...this.state.fishes};
        // update that state
        fishes[key] = updatedFish;
        // set the new fishes object to the state
        this.setState({fishes});

    }

    deleteFish = (key) => {
        // take the copy of state
        const fishes = {...this.state.fishes};
        // update the state
        fishes[key] = null;
        // set the state
        this.setState({fishes});
    }

    deleteOrder = (key) => {
        // take the copy of state
        const order = {...this.state.order};
        // update the state
        delete order[key];
        // set the state
        this.setState({order});
    }

    addFish = fish => {
        // take the copy of current state
        const fishes = {...this.state.fishes};
        // get the new state
        fishes[`fish${Date.now()}`] = fish;
        // set the new state
        this.setState({
            fishes:fishes,
        })
    }

    loadSampleFishes = () =>{
        this.setState({fishes : sampleFishes});
    }

    addToOrder = key => {
        // take the copy of state
        const order = {...this.state.order};
        // change or update the state
        order[key] = order[key] + 1 || 1;
        // set the state
        this.setState({order:order});
    }

    render(){
        return(
            <div>
                <div>
                    <Header
                        addFish={this.addFish}
                        deleteFish = {this.deleteFish} 
                        updateFish = {this.updateFish}
                        loadSampleFishes={this.loadSampleFishes}
                        fishes ={this.state.fishes}
                        storeId = {this.props.match.params.storeId}
                        history={this.props.history}
                    />
                </div>
                <div className="container-dashboard">
                <div className="menu">
                    <RoyalHeader/>
                    <ul className="fishes">
                        {Object.keys(this.state.fishes).map((key) => 
                        <Menu 
                        key={key} 
                        index = {key}
                        details={this.state.fishes[key]} 
                        addToOrder={this.addToOrder}/>
                        )}
                    </ul>
                </div>
                <div className="order">
                    <Order 
                        fishes={this.state.fishes} 
                        order={this.state.order}
                        deleteOrder={this.deleteOrder}/>
                </div>
                </div>
            </div>

        )
    }
}

export default App;
