import React from 'react';
import PropTypes from 'prop-types';

class EditMenuForm extends React.Component {

    static propTypes = {
        fish : PropTypes.shape({
            image : PropTypes.string,
            name : PropTypes.string,
            price : PropTypes.number,
            desc : PropTypes.string,
            status : PropTypes.string,
        }),
        index : PropTypes.string,
        deleteMenuItem : PropTypes.func,
        updateMenuItem : PropTypes.func,
    }
    handleChange = (event) => {
        // update that fish
        // Take the copy of the current fish
        const updatedFish = {
            ...this.props.fish,
            [event.currentTarget.name] : event.currentTarget.value
        };
        this.props.updateMenuItem(this.props.index,updatedFish);

    }
    render() {
        return (
            <div className="fish-edit">
            <input 
                type="text" 
                name = "name" 
                onChange = {this.handleChange} 
                value = {this.props.fish.name}
            />
            <input 
                type="text" 
                name = "price" 
                onChange = {this.handleChange} 
                value = {this.props.fish.price}
            />
            <select 
                type="text" 
                name = "type" 
                onChange = {this.handleChange} 
                value= {this.props.fish.type}
            >
                <option value="available">Fresh!</option>
                <option value="unavailable">Sold Out!</option>
            </select>
            <textarea 
                type="text" 
                name = "desc" 
                onChange = {this.handleChange} 
                value = {this.props.fish.desc}
            />
            <input 
                type="text" 
                name = "image" 
                onChange = {this.handleChange} 
                value= {this.props.fish.image}
            />
            <button onClick={() => {this.props.deleteMenuItem(this.props.index)}}>Remove Menu Item</button>
            </div>
        )
    }
}

export default EditMenuForm;
