import React, { Component } from 'react';
import { Collapse, Button, CardBody, Card } from 'reactstrap';
import MenuItem from './MenuItem';

class Menu extends Component {

    state = { 
        collapse: false,
        voorgerechten:false,
        nagerechten:false,
        hoofdgerechten:false, 
    };

    toggle = (e) => {
    const name = e.target.name;
    this.setState({ [name]: `!this` });
    }

    render() {
    return (
        <div>
            <Button color="primary" name="voorgerechten" onClick={(e) => this.toggle(e)} style={{ marginBottom: '1rem',width:100 + '%' }}>Voorgerechten</Button>
            <Collapse isOpen={this.state.voorgerechten}>
                <MenuItem
                    key={this.props.key} 
                    index = {this.props.key}
                    details={this.props.details} 
                    addToOrder={this.props.addToOrder}
                />
            </Collapse>
            <Button color="primary" name="hoofdgerechten" onClick={(e) => this.toggle(e)} style={{ marginBottom: '1rem',width:100 + '%' }}>Hoofdgerechten</Button>
            <Collapse isOpen={this.state.hoofdgerechten}>
                <MenuItem
                    key={this.props.key} 
                    index = {this.props.key}
                    details={this.props.details} 
                    addToOrder={this.props.addToOrder}
                />
            </Collapse>
            <Button color="primary" name="nagerechten" onClick={(e) => this.toggle(e)} style={{ marginBottom: '1rem',width:100 + '%' }}>Nagerechten</Button>
            <Collapse isOpen={this.state.nagerechten}>
                <MenuItem
                    key={this.props.key} 
                    index = {this.props.key}
                    details={this.props.details} 
                    addToOrder={this.props.addToOrder}
                />
            </Collapse>
        </div>
        );
    }
}

export default Menu;
