import React from 'react';
import Order from './Order';
import RoyalHeader from './RoyalHeader';
import Header from './Header';
import sampleFishes from '../sample-fishes';
import Menu from './Menu';
import base from '../base';
import PropTypes from 'prop-types';

class App extends React.Component{
    state = {
        menuItems:{},
        order:{}
    }

    static propTypes = {
        match : PropTypes.object,
    };

    componentDidMount() {
        const {params} = this.props.match;
        const localStorageRef = localStorage.getItem(params);
        if(localStorageRef){
            this.setState({order:JSON.parse(localStorageRef)})
        }
        console.log
        this.ref = base.syncState(`${params}/menuItems`,{
            context:this,
            state:'menuItems'
        });
    }

    componentDidUpdate() {
        localStorage.setItem(this.props.match.params, JSON.stringify(this.state.order));
    }

    componentWillUnmount() {
        base.removeBinding(this.ref);
    }

    updateMenuItem = (key, updatedMenuItem) => {
        // take the copy of the current state
        const menuItems = {...this.state.menuItems};
        // update that state
        menuItems[key] = updatedMenuItem;
        // set the new fishes object to the state
        this.setState({menuItems});

    }

    deleteMenuItem = (key) => {
        // take the copy of state
        const menuItems = {...this.state.menuItems};
        // update the state
        menuItems[key] = null;
        // set the state
        this.setState({menuItems});
    }

    deleteOrder = (key) => {
        // take the copy of state
        const order = {...this.state.order};
        // update the state
        delete order[key];
        // set the state
        this.setState({order});
    }

    addMenuItem = menuItem => {
        // take the copy of current state
        const menuItems = {...this.state.menuItems};
        // get the new state
        menuItems[`menuItem${Date.now()}`] = menuItem;
        // set the new state
        this.setState({
            menuItems:menuItems,
        })
    }

    loadSampleMenuItems = () =>{
        this.setState({menuItems : sampleFishes});
    }

    addToOrder = key => {
        // take the copy of state
        const order = {...this.state.order};
        // change or update the state
        order[key] = order[key] + 1 || 1;
        // set the state
        this.setState({order:order});
    }

    render(){
        return(
            <div>
                <div>
                    <Header
                        addMenuItem={this.addMenuItem}
                        deleteMenuItem = {this.deleteMenuItem} 
                        updateMenuItem = {this.updateMenuItem}
                        loadSampleMenuItems={this.loadSampleMenuItems}
                        menuItems ={this.state.menuItems}
                        history={this.props.history}
                    />
                </div>
                <div className="container-dashboard">
                    <div className="menu">
                        <RoyalHeader/>
                        <ul className="fishes">
                            {Object.keys(this.state.menuItems).map((key) => 
                            <Menu 
                            key={key} 
                            index = {key}
                            details={this.state.menuItems[key]} 
                            addToOrder={this.addToOrder}/>
                            )}
                        </ul>
                    </div>
                    <div className="order">
                        <Order 
                            fishes={this.state.menuItems} 
                            order={this.state.order}
                            deleteOrder={this.deleteOrder}/>
                    </div>
                </div>
            </div>

        )
    }
}

export default App;
